let messages = [];

function sendMessage() {
    const messageInput = document.getElementById('messageInput').value;
    if (messageInput.trim() !== '') {
        const chatMessages = document.getElementById('chatMessages');
        const sentMessage = document.createElement('div');
        sentMessage.className = 'sent-message';
        sentMessage.textContent = messageInput;
        chatMessages.appendChild(sentMessage);
        document.getElementById('messageInput').value = '';
        sentMessage.style.display = 'block';
    }
}

document.querySelector('.contact-list').addEventListener('click', function (event) {
    var contactItem = event.target.closest('.contact-item');

    if (contactItem) {
        var name = contactItem.querySelector('.contact-details span').innerText;
        var imageSrc = contactItem.querySelector('.contact-image').src;

        updateContactChatHeader(name, imageSrc);
    }
});

function updateContactChatHeader(name, imageSrc) {
    var contactHeader = document.querySelector('.contact-header');
    var contactHeaderImage = document.querySelector('.contact-header-image');
    var contactHeaderText = document.querySelector('.chat-header span');

    contactHeaderImage.src = imageSrc;
    contactHeaderText.innerText = name;
}

